	public static void solve(char[][] board) {
		if (board.length == 0){
			return;
		}
		if (board[0].length == 0){
			return;
		}
		boolean cleared [][] = new boolean [board.length][board[0].length];
		for (boolean [] row: cleared){
			Arrays.fill(row, false);
		}
		
		// clear top and bottom rows
		for (int i=0;i<board[0].length;i++){
			if (board[0][i] == 'O'){
				cleared[0][i] = true;
			}
			if (board[board.length - 1][i] == 'O'){
				cleared[board.length - 1][i] = true;
			}
		}
		
		// clear left and right columns
		for (int i=0;i<board.length;i++){
			if (board[i][0] == 'O'){
				cleared[i][0] = true;
			}
			if (board[i][board[0].length - 1] == 'O'){
				cleared[i][board[0].length - 1] = true;
			}
		}
		
		for (int count = 0;count < board.length * board[0].length; count++){
			boolean changed = false;
			for (int i=0;i<board.length;i++){
				for (int j=0;j<board[i].length;j++){
					if (cleared[i][j]){
						if (i > 0){
							if (board[i-1][j] == 'O'){
								cleared[i-1][j] = true;
								changed = true;
							}
						}
						if (i < board.length-1){
							if (board[i+1][j] == 'O'){
								cleared[i+1][j] = true;
								changed = true;
							}
						}
						if (j > 0){
							if (board[i][j-1] == 'O'){
								cleared[i][j-1] = true;
								changed = true;
							}
						}
						if (j < board[i].length-1){
							if (board[i][j+1] == 'O'){
								cleared[i][j+1] = true;
								changed = true;
							}
						}
					}
				}
			}
			if (!changed){
				break;
			}
		}
		for (int i=0;i<cleared.length;i++){
			for (int j=0;j<cleared[i].length;j++){
				if (!cleared[i][j]){
					board[i][j] = 'X';
				}
			}
		}
	}	
	public static void solve_orig(char[][] board) {
		if (board.length == 0){
			return;
		}
		if (board[0].length == 0){
			return;
		}
		boolean cleared [][] = new boolean[board.length][board[0].length];
		for (boolean [] row: cleared){
			Arrays.fill(row, false);
		}
		
		boolean checked [][] = new boolean[board.length][board[0].length];
		for (boolean [] row: checked){
			Arrays.fill(row, false);
		}
		
        for (int i=0;i<board.length;i++){
        	for (int j=0;j<board[i].length;j++){
        		if (!cleared[i][j]){
        			if (board[i][j] == 'X'){
        				cleared[i][j] = true;
        			} else {
        				boolean surround = is_surround(board, i, j, checked);
        				if (surround){
        					for (int k=0;k<checked.length;k++){
        						for (int l=0;l<checked[k].length;l++){
        							if (checked[k][l]){
        								board[k][l] = 'X';
        								cleared[k][l] = true;
        								checked[k][l] = false;
        							}
        						}
        					}
        				} else {
        					for (int k=0;k<checked.length;k++){
        						for (int l=0;l<checked[k].length;l++){
        							if (checked[k][l]){
        								cleared[k][l] = true;
        							}
        						}
        					}
        				}
        			}
        		}
        	}
        }
    }
	public static boolean is_surround(char[][] board, int row, int column, boolean[][] checked){
		if (row >= board.length || column >= board[0].length || row < 0 || column < 0){
			return false;
		}
		if (checked[row][column]){
			return true;
		}
		if (board[row][column] == 'X'){
			return true;
		}
		else {
			checked[row][column] = true;
			// check all directions that are not in 
			return is_surround(board, row+1, column, checked)
				&& is_surround(board, row-1, column, checked)
				&& is_surround(board, row, column+1, checked)
				&& is_surround(board, row, column-1, checked);
		}
	}
